{
  "events": [
    {
      "id": "ch_1_1_event_1",
      "name": "冒险的开端：白瓷与誓言",
      "description": "在边境小镇的冒险者公会，领取白瓷等级的身份牌，立志成为冒险者。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "location",
            "params": {
              "area_id": "frontier_town"
            }
          },
          {
            "type": "npc_interacted",
            "params": {
              "npc_id": "guild_girl",
              "min_interactions": 1
            }
          }
        ]
      },
      "narrative_directive": "描述公会内喧闹的氛围与柜台小姐职业化的笑容。强调获得白瓷身份牌时的兴奋感，以及对未来的憧憬。"
    },
    {
      "id": "ch_1_1_event_2",
      "name": "轻率的组队：初出茅庐的同行者",
      "description": "与其他几位新手冒险者组成临时队伍，气氛融洽但缺乏专业性。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_1"
            }
          },
          {
            "type": "npc_interacted",
            "params": {
              "npc_id": "rookie_warrior",
              "min_interactions": 1
            }
          }
        ]
      },
      "narrative_directive": "引导玩家与新手战士、见习圣女、红发少女魔法师等角色互动。体现队伍初次组队的热情与友善，但暗示彼此配合生疏，且对即将到来的危险准备不足（如缺乏药水、火把）。"
    },
    {
      "id": "ch_1_1_event_3",
      "name": "深入黑暗：无知的洞穴探索",
      "description": "队伍进入哥布林巢穴，环境变得压抑，队伍阵型暴露弱点。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_2"
            }
          }
        ]
      },
      "narrative_directive": "描述洞穴内的潮湿、恶臭与黑暗。重点描写队伍站位问题：近战成员过于靠前，导致后排的魔法师与神官背后空虚。强调感官上的不适而非理性的战术分析。"
    },
    {
      "id": "ch_1_1_event_4",
      "name": "柜台小姐的告知",
      "description": "在探索中回想起或意识到出发前柜台小姐关于哥布林残忍习性的警告。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_3"
            }
          }
        ]
      },
      "narrative_directive": "通过回忆或环境细节（如发现图腾、残骸），引出柜台小姐曾提及的警告。对比队伍此刻的轻敌心态与残酷的现实，营造不安的氛围。"
    },
    {
      "id": "ch_1_1_event_5",
      "name": "绝望的伏击：新手的末路",
      "description": "哥布林发起突袭，队伍因恐慌和指挥失误瞬间崩溃。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_4"
            }
          }
        ]
      },
      "narrative_directive": "描述突如其来的袭击。体现新手战士因看到同伴受辱或受伤而热血上头，脱离阵型独自冲锋，导致队伍彻底失去防线。强调魔法使用时机的错误和对物品利用的匮乏。"
    },
    {
      "id": "ch_1_1_event_6",
      "name": "不祥的救星：哥布林杀手的登场",
      "description": "在绝望之际，身穿脏兮兮铠甲的哥布林杀手出现。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_5"
            }
          },
          {
            "type": "npc_interacted",
            "params": {
              "npc_id": "goblin_slayer",
              "min_interactions": 1
            }
          }
        ]
      },
      "narrative_directive": "描述哥布林杀手冷酷、务实且毫无英雄气概的形象。他的出现不像是光辉的救赎，更像是某种不祥的死神，但确实带来了生存的希望。"
    },
    {
      "id": "ch_1_1_event_7",
      "name": "冰冷的战术：压倒性的剿灭",
      "description": "哥布林杀手展示高效、残酷的剿灭手段。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_6"
            }
          }
        ]
      },
      "narrative_directive": "详细描写哥布林杀手如何利用环境、道具以及毫不留情的手段清理剩余的哥布林。与之前新手队伍的慌乱形成鲜明对比。"
    },
    {
      "id": "ch_1_1_event_8",
      "name": "幸存者的抉择：踏上新的道路",
      "description": "战斗结束，作为幸存者，决定是否追随哥布林杀手。",
      "is_required": true,
      "trigger_conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_7"
            }
          }
        ]
      },
      "narrative_directive": "处理战斗后的创伤与震撼。引导玩家做出决定：是因恐惧而退缩，还是被这种冷酷的专业性吸引，决定跟随哥布林杀手继续前行。"
    }
  ],
  "transitions": [
    {
      "target_chapter_id": "ch_1_2",
      "conditions": {
        "operator": "and",
        "conditions": [
          {
            "type": "event_triggered",
            "params": {
              "event_id": "ch_1_1_event_8"
            }
          }
        ]
      },
      "priority": 1,
      "transition_type": "normal",
      "narrative_hint": "随着哥布林杀手走出洞穴，外面的阳光显得格外刺眼。一段新的、不同于幻想的冒险生涯开始了。"
    }
  ],
  "pacing": {
    "min_rounds": 5,
    "ideal_rounds": 15,
    "max_rounds": 30,
    "stall_threshold": 6,
    "hint_escalation": [
      "subtle_environmental",
      "npc_reminder",
      "direct_prompt"
    ]
  }
}

{
  "events": [
    {
      "id": "ch_1_2_event_1",
